definition.script:
pipeline {
	agent {
	label {
		label 'built-in'
		customWorkspace '/mnt/project/'
	}
	}
	stages {
		stage ('Clone-repo') {
		steps {
			sh "rm -rf *"
			sh "sudo git clone https://github.com/HarshSinghThakur/game-of-life.git"
			sh "sudo chmod -R 777 /mnt/project"
		}
		}
		stage ('build-war') {
		steps {
			dir ('/mnt/project/game-of-life/') {
			sh "mvn install -Dmaven.test.skip=true"
			}
		}
		}
		stage ('deploy-slave') {
				steps {
				sh 'ansible dev:qa -bm copy -a "src=/mnt/project/game-of-life/gameoflife-web/target/gameoflife.war dest=/mnt/docker-war" '
				sh 'ansible dev:qa -bm copy -a "src=/mnt/docker-war/docker-compose.yaml dest=/mnt/docker-war" '
				}
		}
		stage ('slave-jobs') {
			steps {
			build 'slave-job-compose'
			}
		}

}
}
